// Import Axios
import axios from "axios";

export default {
  methods: {
    fetchDataFromBackend() {
      // Gửi yêu cầu GET tới backend
      axios
        .get("http://localhost:3000/") // Thay đổi URL tương ứng với endpoint bạn muốn truy cập
        .then((response) => {
          // Xử lý dữ liệu nhận được từ backend
          console.log("Dữ liệu từ backend:", response.data);
        })
        .catch((error) => {
          // Xử lý lỗi nếu có
          console.error("Lỗi khi gửi yêu cầu tới backend:", error);
        });
    },
  },
};
